public static void main(String[] args) {
    // TODO Auto-generated method stub

    String str = "I am feeling angry because of this";
    StringBuilder sb = new StringBuilder();
    try {
        StreamTokenizer st = new StreamTokenizer(new StringReader(str), " ");
        List<String> tokens = new ArrayList<>();
        while (st.nextToken()!= StreamTokenizer.TT_EOF) {
            tokens.add(st.nextToken());
        }
        for (String token : tokens) {
            String emotion = detectEmotion(token);
            if (emotion!= null) {
                sb.append(emotion + " ");
            }
        }
    } catch (Exception e) {
        e.printStackTrace();
    }
    System.out.println(sb);
}

private static String detectEmotion(String token) {
    List<String> emotions = new ArrayList<>();
    emotions.add("anger");
    emotions.add("disgust");
    emotions.add("sadness");
    emotions.add("joy");
    emotions.add("neutral");
    emotions.add("surprise");
    emotions.add("fear");
    for (String emotion : emotions) {
        if (token.equalsIgnoreCase(emotion)) {
            return emotion;
        }
    }
    return null;
}

